import { RequestWrapper } from './Helpers';
export function AuthUser(devId, apiKey, resource, referenceId, language, authSuccessRedirectUrl, authFailureRedirectUrl, facilityId) {
    const raw = JSON.stringify({
        reference_id: referenceId,
        resource,
        auth_success_redirect_url: authSuccessRedirectUrl,
        auth_failure_redirect_url: authFailureRedirectUrl,
        language,
        facility_id: facilityId,
    });
    const requestOptions = {
        method: 'POST',
        headers: {
            'X-API-Key': apiKey,
            'dev-id': devId,
            'Content-Type': 'application/json',
        },
        body: raw,
    };
    return RequestWrapper('auth/authenticateUser', requestOptions);
}
