import { RequestWrapper } from './Helpers';
export function GenerateWidgetSession(devId, apiKey, referenceId, language, providers, authSuccessRedirectUrl, authFailureRedirectUrl, showDisconnect) {
    const raw = JSON.stringify({
        reference_id: referenceId,
        providers: providers ? providers.join(',') : undefined,
        auth_success_redirect_url: authSuccessRedirectUrl,
        auth_failure_redirect_url: authFailureRedirectUrl,
        language,
        show_disconnect: showDisconnect,
    });
    const requestOptions = {
        method: 'POST',
        headers: {
            'X-API-Key': apiKey,
            'dev-id': devId,
            'Content-Type': 'application/json',
        },
        body: raw,
    };
    return RequestWrapper('auth/generateWidgetSession', requestOptions);
}
