import fetch from 'cross-fetch';
import { RequestWrapper } from './Helpers';
export function GetUser(devId, apiKey, userID, referenceID) {
    const requestOptions = {
        method: 'GET',
        headers: {
            'X-API-Key': apiKey,
            'dev-id': devId,
            'Content-Type': 'application/json',
        },
    };
    return RequestWrapper('userInfo', requestOptions, {
        user_id: userID,
        reference_id: referenceID,
    });
}
export function DeauthUser(devId, apiKey, userId) {
    const requestOptions = {
        method: 'DELETE',
        headers: {
            'X-API-Key': apiKey,
            'dev-id': devId,
            'Content-Type': 'application/json',
        },
    };
    return new Promise((res, rej) => {
        fetch(`https://api.tryterra.co/v2/auth/deauthenticateUser?user_id=${userId}`, requestOptions)
            .then((_) => res())
            .catch((error) => rej(error));
    });
}
